          <div class="hamburgerBottom block absolute bg-black" :class="{'open': open}"></div>
        </div>

        <span class="-mt-1 ml-2 inline-block text-xl leading-none">Menu</span>
      </button>
    </div>
  </header>
</template>

<script>
export default {
  props: {
    mode: {
      type: String,
      default: 'light'
    }
  },
  data() {
    return {
      open: false
    }
  },
  mounted () {
    const header = document.getElementsByTagName('header')[0]
    const blurredImg = document.getElementsByClassName('blurred-img')[0]
    const banner = document.getElementById('banner')
    const carousel = document.getElementById('carousel')
    const primaryText = this.mode === 'light' ? 'text-white' : 'text-dark'
    let bannerCalc = 0

    if (banner) {
      bannerCalc = (banner.offsetTop + banner.offsetHeight) - header.offsetHeight
    } else if (carousel) {
      bannerCalc = (carousel.offsetTop + carousel.offsetHeight) - header.offsetHeight
    }

    window.onscroll = () => {
      if (blurredImg) {
        blurredImg.style.opacity = window.scrollY / 150
      }

      if ((banner || carousel) && window.scrollY <= header.offsetHeight && !header.classList.contains('bg-dark')) {
        if (window.scrollY > bannerCalc) {
          header.classList.add('text-dark')
          header.classList.remove('text-white')
        } else {
          header.classList.remove('text-dark')
          header.classList.add('text-white')
        }
      } else {
        header.classList.remove('text-dark')
        header.classList.add('text-white')

        if (window.scrollY > header.offsetHeight) {
          header.classList.add('bg-dark')
          header.classList.add('text-white')
          header.classList.remove('text-dark')
        } else {
          header.classList.remove('bg-dark')
          header.classList.remove('text-white')
          header.classList.remove('text-dark')
          header.classList.add(primaryText)
        }
      }
    }
  },
  methods: {
    toggleMenu() {
      this.open = !this.open

      this.$refs.navigation.style.opacity = this.open ? 1 : 0
      this.$refs.navigation.classList.toggle('hidden')
    }
  }
}
</script>
